import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor() {
    super();
    this.state = { 
      counter: 0,
      seconds:0
     };
  }
  C = () =>{
    setInterval(() =>this.setState({seconds:this.state.seconds+1}),1000)
  } 
  render() {
    return <div>
      <p>Counter:{this.state.counter}</p>
      <p>Seconds from start:{this.state.seconds}</p>
    </div>;
  }
}

export default App;
