import React from "react";
import "./styles/Task.css";
import { useState } from "react";
const url = "http://localhost:5000/api/";

export default function Task({ task, getRemoveTask, updateDone, listShow }) {
  const deadline =
    task.dueDate === null ? "" : task.dueDate.toLocaleString().slice(0, 10);
  const doneChange = () => {
    task.done = task.done ? false : true;
    updateDone(task);
  };
  console.log(url);
  const [listsDTO, setListDTO] = useState(null);
  use
    fetch(url + "/tasktodo/dashboard").
    then((response) => response.json()).
    then(setListDTO)

console.log(setListDTO)
  return (
    <div className="task-block">
      <h1 className="title">{task.title}</h1>
      <div className="description">{task.desc}</div>
      <input type="checkbox" checked={task.done} onChange={doneChange} />
      <div>{deadline}</div>
      <button onClick={() => getRemoveTask(task)}>Delete</button>
      <div>{listShow ? task.taskListId : ""}</div>
    </div>
  );
}
