import React from 'react'
import {useEffect} from 'react';
export default function TodoListSidebar(props) {
    const listTasks = props.listTasks;
    const openListTasks = (event)=>{
        props.getListId(event.target.value)
    }
    useEffect(() => {
        openListTasks(props.getListId)        
    }, [])
    return (
        <div className="todoListSidebar">
            {listTasks.map(e => <button onClick={openListTasks} value={e.id}>{e.title}</button>)}
        </div>
    )
}
