import Task from './Task'
import './styles/Tasks.css'
export default function Tasks({tasks,getRemoveTask,updateDone}) {
    const removeTask = (t) =>{
        fetch(url+`tasktodo/${t.id}`,
    { method:'DELETE',
      headers:{
            'Content-Type':'application/json'},

      body:JSON.stringify(t)
    }).then(setTasks(tasks.filter(task=>t.id !==task.id)))
    }

    return (
        <div id="task-list">
            {tasks.map((t,i) => <Task key={i} task ={t} getRemoveTask={getRemoveTask} updateDone={updateDone}/>)}  
        </div>
    )
}
