import React, { useState } from "react";
import "./App.css";
import NewTaskForm from "./NewTaskForm";
import Tasks from "./Tasks";
import {useEffect} from 'react'
import TodoListSidebar from "./TodoListSidebar";
const lists = [{id:1,title:'today'},{id:2,title:'not-today'}]
const api = 'http://localhost:5000/api/tasktodo'
function App() {
  let [tasks, setTasks] = useState([
    {
      id: 1,
      title: "First",
      desc: "first desc",
      done: true,
      dueDate: new Date("2020-12-12"),
      taskListId: 1,
    },
    {
      id: 2,
      title: "Second",
      desc: "first desc",
      done: true,
      dueDate: new Date("2011-11-11"),
      taskListId: 2,
    },
  ]);
  let [list,setList] = useState(lists[0])

  const removeTask = (t) =>{
      setTasks(tasks.filter(task=>t.id !==task.id))}

  const addTask = (task) => {
    let index = 1
    if (tasks.length !=0){ 
      index = tasks[tasks.length -1].id + 1
      }
  setTasks([...tasks, {...task,taskListId:list.id,id:index}])
  };


  useEffect(() =>{
    fetch(api).then(response=>response.json())
              .then(ts=>setTasks(tasks.concat(ts))
              )
  }
  ,[])  

  let taskList = tasks.filter(t=>t.taskListId ==list.id)

  return (
    <div id="tasks">
      <div className="use-list">
        <TodoListSidebar lists={lists} selectedList={list} onSelect={setList} />
      </div>
      <div className="use-task">
        <Tasks tasks={taskList} getTask={removeTask}/>
        <NewTaskForm onSubmit={addTask} />
      </div>
    </div>
  );
}

export default App;
