import React, { useState } from "react";
import "./App.css";
import NewTaskForm from "./NewTaskForm";
import Tasks from "./Tasks";
import TodoListSidebar from "./TodoListSidebar";
const listTasks = [{id:1,title:'today'},{id:2,title:'not-today'}]

function App() {
  let [tasks, setTasks] = useState([
    {
      id: 1,
      title: "First",
      desc: "first desc",
      done: true,
      dueDate: new Date("2020-12-12"),
      listId: 1,
    },
    {
      id: 2,
      title: "Second",
      desc: "first desc",
      done: true,
      dueDate: new Date("2011-11-11"),
      listId: 2,
    },
  ]);
  let [list,setList] = useState(list)

  const addTask = (task) => {
    setTasks([...tasks, task])
  };
  const getTasksByList = (listId) =>{
    return tasks.filter(t => t.listId ==listId)
  }
  return (
    <div id="tasks">
      <TodoListSidebar listTasks={listTasks} getListId={getTasksByList}/>
      <Tasks tasks={getTasksByList()}/>
      <NewTaskForm onSubmit={addTask} />
    </div>
  );
}

export default App;
