import React from "react";
import Task from "./Task";
import { useState, useEffect } from "react";
import './styles/Tasks.css';
import NewTaskForm from './NewTaskForm'
export default function TodayTasks(props) {
  const url = "http://localhost:5000/api/";
  const [tasks, setTasks] = useState([]);
  useEffect(() => {
    fetch(url+'tasktodo/colections/today')
      .then((response) => response.json())
      .then((ts) => setTasks(ts));
  },[]);
  const removeTask = (t) =>{
    fetch(url+`tasktodo/${t.id}`,
{ method:'DELETE',
  headers:{
        'Content-Type':'application/json'},

  body:JSON.stringify(t)
}).then(setTasks(tasks.filter(task=>t.id !==task.id)))
}

const updateDone =(t)=> {
    fetch(url+`tasktodo/${t.id}`,
  { method:'PATCH',
    headers:{
          'Content-Type':'application/json'},

    body:JSON.stringify(t)
  }).then(response=>response.json()).then(()=> setTasks([...tasks]))
  }
  const addTask = (task) => {
  fetch(url+`tasktodo/`,
  { method:'POST',
    headers:{
          'Content-Type':'application/json'},

    body:JSON.stringify({...task})
  }).then(response=>response.json()).then((task)=> setTasks([...tasks, task]))
}
  return (
    <div id="task-list">
            {tasks.map((t,i) => <Task key={i} task ={t} listShow ={true} getRemoveTask={removeTask} updateDone={updateDone}/>)} 
        <NewTaskForm onSubmit={addTask} /> 

        </div>

  );
}
