import { SHOW_TASKLIST } from './actions'
import {combineReducers} from 'redux'


function taskListReduser(state = {},action){
    switch(action.type){
        case SHOW_TASKLIST:
            return [action.payload]
        default:
            return state
    }
}

export default combineReducers({
    lists: taskListReduser,
})
