import React, { useState } from "react";
import "./App.css";
function App() {
  const [counter,setCounter ] = useState(0)
  setInterval(tick(counter),1000)

  function tick(counter){
    return counter + 1
  }
  function counterAdd(){
    return setCounter(tick)
  }
  return <div>
      <p>Counter:{counter}</p>
      <button onClick={counterAdd}>Add</button>
      <p>Seconds from start:</p>
    </div>;
  }


export default App;
