import React, { useState } from "react";
import "./App.css";
import NewTaskForm from "./NewTaskForm";
import Tasks from "./Tasks";
import TodoListSidebar from "./TodoListSidebar";
const lists = [{id:1,title:'today'},{id:2,title:'not-today'}]

function App() {
  let [tasks, setTasks] = useState([
    {
      id: 1,
      title: "First",
      desc: "first desc",
      done: true,
      dueDate: new Date("2020-12-12"),
      listId: 1,
    },
    {
      id: 2,
      title: "Second",
      desc: "first desc",
      done: true,
      dueDate: new Date("2011-11-11"),
      listId: 2,
    },
  ]);
  let [list,setList] = useState(lists[0])

  const addTask = (task) => {
    setTasks([...tasks, {...task,listId:list.id}])
  };
  let taskList = tasks.filter(t=>t.listId ==list.id)

  return (
    <div id="tasks">
      <div className = "listNames">
        <TodoListSidebar lists={lists} selectedList={list} onSelect={setList} />
      </div>
      <div className="use-task">
      <Tasks tasks={taskList}/>
      <NewTaskForm onSubmit={addTask} />
      </div>
    </div>
  );
}

export default App;
