import React, { useState } from "react";
import "./App.css";
import NewTaskForm from "./NewTaskForm";
import Tasks from "./Tasks";
import {useEffect} from 'react'
import Dashboard from "./Dashboard";
import { Route } from "react-router";
import TodayTasks from './TodayTasks'
const url = 'http://localhost:5000/api/'
function App() {
  const [tasks, setTasks] = useState([]);
  const [taskLists, setTaskLists] = useState([]);
  const [list,setList] = useState({id:1});



  const addTask = (task) => {
    task = {...task,taskListId:list.id}
  fetch(url+`tasktodo/`,
  { method:'POST',
    headers:{
          'Content-Type':'application/json'},

    body:JSON.stringify(task)
  }).then(response=>response.json()).then((task)=> setTasks([...tasks, task]))
}

  useEffect(() =>{
    fetch(url+`tasklist/`).then(response=>response.json())
              .then(function(lists) {
                setTaskLists(lists)
                setList(lists[0])
              })

  }
  ,[])
  useEffect(() => {
    fetch(url+`tasklist/${list.id}/tasks`).
    then(response =>response.json()).
    then(ts=>setTasks(ts))
  }, [list])
  const updateDone =(t)=> {
    fetch(url+`tasktodo/${t.id}`,
  { method:'PATCH',
    headers:{
          'Content-Type':'application/json'},

    body:JSON.stringify(t)
  }).then(response=>response.json()).then(()=> setTasks([...tasks]))
  }


  return (
    <div id="tasks">
      <div className="todoListSidebar">
        <Dashboard lists={taskLists} selectedList={list} onSelect={setList} />
      </div>
      <div className="tasks-info">
        <Tasks tasks={tasks} getRemoveTask={removeTask} updateDone={updateDone}/>
        <NewTaskForm onSubmit={addTask} />
      </div>

    </div>
  );
}

export default App;
