export const SHOW_TASKS = 'SHOW_TASKS'

export const loadTasks = (id) =>dispatch =>{
    fetch('http://localhost:5000/api/tasklist/1/tasks')
    .then(res=>res.json())
    .then(tasks=>dispatch({
        type:SHOW_TASKS,
        payload:tasks
    }))
}
