import React from 'react';
import {useState} from 'react';
import { useEffect } from 'react';
import Task from './Task';
import NewTaskForm from './NewTaskForm';
import  { removeTask,updateDone,createTask } from './task-service/api-service';


export default function NotDone(props) {
    let [tasks,setTasks] = useState([]);
    useEffect(()=>fetch('http://localhost:5000/api/tasktodo').
    then(response=>response.json()).
    then(setTasks)
    ,[]);

    const removeTask = (t) => {
        deleteTask(t).then(setTasks(tasks.filter((task) => t.id !== task.id)));
      };

      const updateDone = (t) => {
        patchTask(t).then(() => setTasks([...tasks]));
      };

      const addTask = (task) => {
        createTask(task, props.list).then((task) => setTasks([...tasks, task]));
      };
    return (
        <div>
           {tasks.map((t, i) => (
        <Task
          key={i}
          task={t}
          getRemoveTask={removeTask}
          updateDone={updateDone}
          lists={props.lists}
          today={true}
        />

      ))}
    <NewTaskForm onSubmit={addTask} />
            </div>
    )
}
