import React, { useState } from "react";
import "./Form.css";
const useFormField = (falue,name) => {
    const [value,setValue] = useState(initialFalue);
    return {
        value:value,
        name:name,
        onChange: (e)=>setValue(e.target.value)
    }
};
export default function NewTaskForm() {
    const title = useFormField('','title')
    const desc = useFormField('','desc')
    const checkbox = useFormField('','done')
    const dueDate = useFormField('','dueDate')

  const onSubmit = (event) => {
    event.preventDefault();
    console.log(event.target.value);
  };
  return (
    <div className="taskForm">
      <form onSubmit={onSubmit}>
        <input type="text" {...title} />
        <input type="text" {...desc} />
        <input type="checkbox" {...checkbox} />
        <input type="date" name="duDate" />
        <input type="submit" value="Отправить" />
      </form>
    </div>
  );
}
