import { SHOW_TASKLIST } from './actions'
import {combineReducers} from 'redux'

function openedTasksReducer(state = {}, action) {
    return state
}
function taskListReduser(state = {},action){
    switch(action.type){
        case SHOW_TASKLIST:
            return state = action.payload
        default:
            return state
    }
}

export default combineReducers({
    lists: (lists = [], {type, payload}) => type === DASHBOARD_LOADED ? payload : lists,
    openedTasks: openedTasksReducer
})
