import React, { useState } from "react";
import "./Form.css";
const useFormField = (init,name) => {
    const [value,setValue] = useState(init);
    return {
        value:value,
        name:name,
        onChange: (e)=>setValue(e.target.value)
    }
};
function buildObject(...fields){
    const object = []
    for (let field of fields){
        object[field.name] = field.value
    }
    return object
}
export default function NewTaskForm(props) {
    const title = useFormField('','title')
    const desc = useFormField('','desc')
    const checkbox = useFormField('','done')
    const dueDate = useFormField('','dueDate')

  const onSubmitHandler = (event) => {
    event.preventDefault();
    console.log(event.target.r)
    let taskForm = buildObject(title,desc,checkbox,dueDate)
    props.onSubmit(taskForm)
};
  return (
    <div className="taskForm">
      <form onSubmit={onSubmitHandler}>
        <input type="text" {...title} />
        <input type="text" {...desc} />
        <input type="date" {...dueDate} />
        <input type="submit"/>
      </form>
    </div>
  );
}
