import React, { useState } from "react";
import "./App.css";
import NewTaskForm from "./NewTaskForm";
import TodoListSidebar from "./TodoListSidebar";

function App() {
  let [tasks, setTasks] = useState([
    {
      id: 1,
      title: "First",
      desc: "first desc",
      done: true,
      dueDate: new Date("2020-12-12"),
      listId: 1,
    },
    {
      id: 2,
      title: "Second",
      desc: "first desc",
      done: true,
      dueDate: new Date("2011-11-11"),
      listId: 2,
    },
  ]);
  let [listTasks, setList] = useState([
    {
      id: 1,
      title: "today",
      taskList: tasks,
    },
    { id: 2, title: "notToday", taskList: tasks },
  ]);

  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  return (
    <div id="tasks">
      <TodoListSidebar listTasks={listTasks} />
      <NewTaskForm onSubmit={addTask} />
    </div>
  );
}

export default App;
