import React from 'react'
import './Task.css';

export default function Task({task}) {
    let setDate = (date) => date.toLocaleString().slice(0,10)
    const deadline = setDate(task.dueDate)
    return (
        <div className ="task-block" id={`task-${task.id}`}>
            <h1 className="title">{task.title}</h1>
            <div className="description">{task.desc}</div>
            <input type="checkbox"/>
            <div>{deadline}</div>

        </div>
    )
}
