import React from 'react'
import Task from './Task'
import './styles/Tasks.css'
export default function Tasks({list}) {
    const [tasks, setTasks] = useState([]);

    const removeTask = (t) =>{
        fetch(url+`tasktodo/${t.id}`,
    { method:'DELETE',
      headers:{
            'Content-Type':'application/json'},

      body:JSON.stringify(t)
    }).then(setTasks(tasks.filter(task=>t.id !==task.id)))
    }

    const updateDone =(t)=> {
        fetch(url+`tasktodo/${t.id}`,
      { method:'PATCH',
        headers:{
              'Content-Type':'application/json'},

        body:JSON.stringify(t)
      }).then(response=>response.json()).then(()=> setTasks([...tasks]))
      }

      useEffect(() => {
        fetch(url+`tasklist/${list.id}/tasks`).
        then(response =>response.json()).
        then(ts=>setTasks(ts))
      }, [list])

  const addTask = (task) => {
    task = {...task,taskListId:list.id}
  fetch(url+`tasktodo/`,
  { method:'POST',
    headers:{
          'Content-Type':'application/json'},

    body:JSON.stringify(task)
  }).then(response=>response.json()).then((task)=> setTasks([...tasks, task]))
}

    return (
        <div id="task-list">
            {tasks.map((t,i) => <Task key={i} task ={t} getRemoveTask={removeTask} updateDone={updateDone}/>)}  
        </div>
    )
}
