import React from 'react'
import {useRef} from 'react';
export default function TodoListSidebar(props) {
    const listTasks = props.listTasks;
    const openList = useRef(listTasks[0])
    const openTaskList = () =>{
        props.getListId()
    }
    return (
        <div className="todoListSidebar">
            {listTasks.map((e,id) => <button onClick={openTaskList}>{e}</button>)}
        </div>
    )
}
